if (!isset($_SERVER['HTTP_HOST'])) {
    exit('This script cannot be run from the CLI. Run it from a browser.');
}

require_once dirname(__FILE__).'/../app/KunstmaanRequirements.php';

$requirements = new KunstmaanRequirements(isset($_GET['ehost']) ? $_GET['ehost'] : null, isset($_GET['eport']) ? $_GET['eport'] : null);

$majorProblems = $requirements->getFailedRequirements();
$minorProblems = $requirements->getFailedRecommendations();

$whiches = $requirements->getAllWhiches();

?>
